(105, 'torts', '14. Defense of Consent?', NULL, NULL, NULL, NULL, '2017-09-24 23:05:46', 1),
(106, 'torts', '15. Defense of Others?', NULL, NULL, NULL, NULL, '2017-09-24 23:06:04', 1),
