(39, 'contracts', '39. STANDING based on VALID ASSIGNMENT?', '<p><br></p><p>\r\n		\r\n	\r\n	\r\n		</p><div class=\"page\" title=\"Page 7\">\r\n			<div class=\"layoutArea\">\r\n				<div class=\"column\">\r\n					<p><span style=\"font-size: 12.000000pt; font-family: \'ArialMT\'\">An assignment is the transfer of a contractual right to a third party.&nbsp;</span></p><p>\r\n		\r\n	\r\n	\r\n		</p><div class=\"page\" title=\"Page 7\">\r\n			<div class=\"layoutArea\">\r\n				<div class=\"column\">\r\n					<p><span style=\"font-size: 12.000000pt; font-family: \'ArialMT\'\">An assignment that\r\ndeclares \"all of my rights\" or similar language is both an assignment of rights and a\r\ndelegation of duties.&nbsp;</span></p>\r\n				</div>\r\n			</div>\r\n		</div>\r\n				</div>\r\n			</div>\r\n		</div>', NULL, NULL, '<p><br></p>', '2017-09-27 14:47:22', 1),
(40, 'contracts', '40. VALID DELEGATION of performance?', '<p><br></p><p>\r\n		\r\n	\r\n	\r\n		</p><div class=\"page\" title=\"Page 7\">\r\n			<div class=\"layoutArea\">\r\n				<div class=\"column\">\r\n					<p><span style=\"font-size: 12.000000pt; font-family: \'ArialMT\'\">A delegation is the transfer of a contractual duty to a third party.</span></p>\r\n		\r\n	\r\n	\r\n		<p><span style=\"font-family: ArialMT; font-size: 12pt;\">An assignment that\r\ndeclares \"all of my rights\" or similar language is both an assignment of rights and a\r\ndelegation of duties.</span><span style=\"font-size: 12.000000pt; font-family: \'ArialMT\'\">&nbsp;</span></p>\r\n				</div>\r\n			</div>\r\n		</div>', NULL, NULL, NULL, '2017-09-27 14:47:13', 1),
