INSERT INTO `religions_view` (`view_id`, `view_user_id`, `religion_id`, `view_description`, `category_id`, `view_created_dt`, `view_status`, `view_images`, `view_videos`, `view_links`, `from_religion_id`, `from_verse_id`, `chapter`) VALUES
(1, 1, 1, 'Go beyond yourself rather than beyond others.', 1, '2017-09-13 05:30:24', 1, '[]', '[]', '[]', NULL, NULL, NULL),
(2, 1, 1, 'Know your limits but never stop trying to exceed them.', 1, '2017-09-13 16:43:23', 1, '[]', '[]', '[]', NULL, NULL, NULL),
(3, 1, 1, 'Go beyond yourself rather than beyond others.', 1, '2017-09-13 22:00:46', 3, '[]', '[]', '[]', 1, 1, NULL),
(4, 1, 1, 'Know your limits but never stop trying to exceed them.', 1, '2017-09-13 22:00:46', 3, '[]', '[]', '[]', 1, 2, NULL),
(5, 1, 1, 'Life begins at the end of your comfort zone.', 1, '2017-09-14 14:30:37', 1, '[]', '[]', '[]', NULL, NULL, NULL),
(6, 1, 1, 'People who are unable to motivate themselves must be content with mediocrity no matter how impressive their other talents.', 1, '2017-09-15 16:19:50', 1, '[]', '[]', '[]', NULL, NULL, NULL),
(7, 1, 1, 'Whenever you are confronted with an opponent, conquer them with love.', 1, '2017-09-17 22:11:51', 1, '[]', '[]', '[]', NULL, NULL, NULL);
INSERT INTO `rules` (`rule_id`, `rule`, `description`, `rule_subject`) VALUES
(1, 'Governing Law', 'The Uniform Commercial Code (UCC) applies to the sale of goods, which are tangible, movable personal property identified to the contract at the time of formation.\r\n\r\nCommon law governs all other contracts.', 'Contracts Law'),
(2, 'Merchant', 'A merchant is a person who regularly deals in these kinds of goods or who otherwise holds themself out as having special knowledge as to these goods.', 'Contracts Law'),
(3, 'Contract Formation', 'Contract formation requires a valid offer, a valid acceptance, consideration, and no valid defenses.', 'Contracts Law'),
(4, 'Offer', 'An offer is the outward manifestation of present contractual intent, using definite and certain terms, communicated to the offeree.  Under the common law, an offer must sufficiently describe the parties, the subject matter, the quantity, the price, and the time of delivery. Under the U.C.C., however, price and time may be omitted and a court will substitute reasonable terms for those that are missing.', 'Contracts Law'),
(5, 'Termination', 'Termination can happen in one of three ways; either by rejection, revocation, or destruction of subject matter.', 'Contracts Law'),
(6, 'Rejection', 'A rejection is a manifestation by the offeree that he or she does not intend to accept the offer nor to give it further consideration.', 'Contracts Law'),
(7, 'Acceptance', 'An acceptance under common law is an unequivocal assent to the terms of an offer. An acceptance under the UCC is any seasonable expression of acceptance.', 'Contracts Law'),
(8, 'Mailbox Rule', 'Under the Mailbox Rule, established in the case of Adams v. Lindsell, an acceptance of an offer for a bilateral contract, dispatched by an authorized mode of communication, is effective when mailed.', 'Contracts Law'),
(9, 'Consideration', 'Consideration is that which is bargained for and given in exchange for a promise.', 'Contracts Law'),
(10, 'Differing Terms Battle Of Forms', 'Where a merchant sends a letter of confirmation containing different or added terms that adds to, varies or materially alters a contract the different or added terms will be \"knocked out\" and only the agreed upon terms will be retained within the contract unless the other party fails to reject the added terms in a reasonable amount of time, typically within 10 days of receipt.', 'Contracts Law'),
(11, 'Statute Of Fraud', 'The statute of frauds state that certain contracts must be in writing to be enforceable.\r\n\r\nThose contracts which are customarily within the Statute of Frauds are:\r\n- A contract which, by its terms, cannot be performed within one year from the making thereof,\r\n- A promise to answer for the debt or default of another,\r\n- A promise in consideration \r\n- A contract involving an interest in real property, and\r\nUnder the UCC, a contract for the sale of goods priced $500 or more.\r\n', 'Contracts Law'),
(19, 'Incidental Damage', 'A non-breaching party can recover incidental damages for out-of-pocket expenses naturally flowing from the breach; i.e. hiring a broker, placing an ad, etc.', 'Contracts Law'),
(13, 'Confirmatory Letter', 'There are, however, exceptions to the Statute of Frauds that when satisfied will take a contract \"out of\" the Statute of Frauds. Once such exception is a confirmatory letter between merchants: a letter confirming a prior agreement to enter into a contract typically within the statute of frauds. The Defendant has 10 days to object to the confirmatory letter after which it becomes binding as a matter of law.', 'Contracts Law'),
(14, 'Merchant Constructive Condition', 'Merchants are held to a higher standard of good faith and fair dealing under an implied constructive condition.', 'Contracts Law'),
(15, 'Breach', 'A failure to perform under the obligations of the contract.\r\n\r\nA minor breach is when the breaching party has substantially performed under the contract, but not perfectly. \r\n\r\nA major breach is when the non-performance of the duty substantially impairs the expected benefit of the bargain for the non-breaching party.', 'Contracts Law'),
(16, 'Parol Evidence Rule', 'Any oral or written communication made prior to or contemporaneous with a fully integrated writing will be barred by the parol evidence rule.', 'Contracts Law'),
(17, 'Remedies', 'Where a party has breached their contractual duties the aggrieved party is able to recover damages that naturally flow from the breach.', 'Contracts Law'),
(18, 'Expected Damage', 'An aggrieved party to a contract may recover for damages they expected under the contract. The formula utilized by the court is the fair market value of the subject manner minus the contract price.', 'Contracts Law'),
(20, 'Special Performance', 'Specific performance is an equitable remedy whereby a court requires a party to perform under the contract. Specific performance is typically awarded if monetary damages are inadequate to make the aggrieved party whole or where the subject matter is particularly unique.', 'Contracts Law'),
(21, 'Compensation Damage', 'Compensation damages must be reasonably foreseeable and the plaintiff must mitigate.', 'Contracts Law'),
(22, 'U.C.C. Merchant\'s Confirmatory Memorandum', 'A contract that would otherwise be unenforceable under the Statute of Frauds may still be enforceable if a merchant seller issues a signed writing confirming the deal.', 'Contracts Law'),
(23, 'Anticipatory Breach', 'When one party unequivocally states that they will not perform their duties under a contract before performance is due that is an anticipatory breach.', 'Contracts Law'),
(24, 'Modification', 'A modification is when a party seeks to change the terms of an existing contract.', 'Contracts Law'),
(25, 'Performance of the contract', 'When any conditions are met or excused, the duties of the parties come due. If these duties are not performed then the result is a breach of contract, unless the duties can be discharged.', 'Contracts Law'),
(26, 'Discharge of duties', 'When the party due to perform claims that they cannot because of an unforeseen event that has occurred.', 'Contracts Law'),
(27, 'Impossibility of Performance', 'Impossibility is when the party states that it is impossible for them to perform. This impossibility must be objective, to where no one in the world could perform the duty.', 'Contracts Law'),
(28, 'Impracticability', 'Impracticability is when the party claims that performance would give them a substantial hardship.', 'Contracts Law'),
(29, 'Mistake', 'Mistake is the last chance Dealer would have to avoid the contract would be that she was mistaken as to the true value of the object.\r\n', 'Contracts Law'),
(30, 'Unilateral mistake', 'A unilateral mistake where only one party is mistaken will have no effect unless the other party knew or should have known of the mistake. ', 'Contracts Law'),
(31, 'Mutual Mistake', 'Mutual mistake where both parties are mistaken as to a material term of contract can make the contract voidable by either party. \r\n', 'Contracts Law'),
(32, 'Hybrid Contract', 'The majority of jurisdictions will determine whether the thrust of the contract was for goods or services and use the thrust as the basis for determining governing law. \r\n\r\nA minority of jurisdictions apply the Gravamen test where the goods portion of the contract is governed by the UCC and the services component is governed by the common law.', 'Contracts Law'),
(33, 'Mutual Assent', 'Where, the parties through a written agreement and/or their conduct, have expressly or impliedly demonstrated a mutual intent to be bound to the terms of a contract, mutual assent (consisting of a valid offer and acceptance) can be demonstrated.', 'Contracts Law'),
(34, 'Third Party Beneficiary', 'A third party beneficiary contract is one in which performance by the promisor will benefit a third party, i.e. a person or entity other than the promisee.\r\n', 'Contracts Law'),
(35, 'Privity in Third Party Beneficiary', 'Under the contract law, privity is required for a person to have standing to sue to enforce a contract, unless the third party beneficiary an intended beneficiary', 'Contracts Law'),
(36, 'Donee beneficiary', 'If the promisee\'s intent in extracting a performance from promisor was to make a gift, the 3rd party beneficiary is a donee beneficiary.', 'Contracts Law'),
(37, 'Creditor Beneficiary', 'If the promisee\'s intent in extracting a performance from promisor was to satisfy of actual or supposed debt or obligation, then 3rd party beneficiary is a creditor beneficiary.\r\n', 'Contracts Law'),
(38, 'Vesting in Third Party Beneficiary', 'Under the majority rule, the rights of an intended beneficiary vest when he has learned of the existence of the contract and assented to it. Under the minority rule, the rights of an intended beneficiary vest when he has detrimentally relied on the contract. \r\n', 'Contracts Law'),
(39, 'Divide Lawsuits in Third Party Beneficiary', '3 people , A promissor, B promisee, C is Third Party\r\nWhether A can sue B - they can sue as they are in privity\r\nWhether A can sue C - A cannot sue C\r\nWhether B can sue A - they can sue as they are in privity\r\nWhether B can sue C - B cannot sue C\r\nWhether C can sue A - If Câ€™s right has vested and C is intended benefit, Incidental beneficiary can never sue.\r\nWhether C can sue B - Intended Creditor Beneficiary can sue based on their previous contract. Donee Beneficiary cannot sue unless there is foreseeable and Detrimental reliance. Incidental beneficiary cannot sue the Promisee.\r\n', 'Contracts Law'),
(40, 'Assignments', 'A transfer of a contractual right by the promisee to third party, and, is made after the formation of contract.\r\n\r\nDepraved', 'Contracts Law'),
(41, 'Privity in Assignments', 'Privity is generally not required.\r\n', 'Contracts Law'),
(42, 'Rights Assignable', 'Contract favors the free assignability of contract rights. Therefore rights are assignable unless they are too personal or prohibited by contracts.\r\n', 'Contracts Law'),
(43, 'Valid Present Assignment', 'Generally an anti-assignment clause only destroys the right to assign, not the power. To destroy the power, the contract language would have to say: â€œAll assignments are null and void.â€ This language should be present in facts to make assignment null.\r\nElse assignment is effective.\r\n', 'Contracts Law'),
(44, 'Effect in Assignment', 'The effect of a valid assignment is that the assignee steps into the shoes of the assignor  and enjoys all rights to enforcement.\r\n \r\n', 'Contracts Law'),
(45, 'Defenses in Assignments', 'A can raise any defense against D which A can raise against B.\r\n', 'Contracts Law'),
(46, 'Delegation', 'A delegation of duty is a transfer of a contractual obligation by the promisor made after the formation of contract, in which the promisor transfers his or her duty of performance to a third party.\r\n\r\nDiane', 'Contracts Law'),
(47, 'Time of Essence', 'Where a contract unequivocally states specific dates in which the contract must be performed, time is of the essence and failure to deliver duties in that period is considered a material breach.', 'Contracts Law'),
(48, 'Advertisement', 'An advertisement is generally not considered an offer. However if the advertisement\r\nhas clear and definite terms such that the advertisement may meet the requirements\r\nfor an offer, some courts will allow for an advertisement to be considered an offer.', 'Contracts Law');
INSERT INTO `sites` (`site_id`, `site_name`, `site_title`, `site_domain`, `site_subtitle`, `site_background_img`, `site_rightside_img`, `site_icon1`, `site_icon1_title`, `site_icon1_desc`, `site_icon2`, `site_icon2_title`, `site_icon2_desc`, `site_icon3`, `site_icon3_title`, `site_icon3_desc`, `site_icon4`, `site_icon4_title`, `site_icon4_desc`, `site_findoutmorelink`, `site_sec_title`, `site_sec_desc`, `site_sec_link`, `site_default_title`, `site_default_desc`, `site_default_image`, `site_primary_title`, `site_primary_desc`, `site_primary_image`, `site_our_team`, `site_about_title`, `site_about_desc`, `site_about_image`, `site_address`, `site_phone`, `site_email`, `site_timings`, `site_links`, `site_admin_email`, `site_lat`, `site_lng`) VALUES
(1, 'My Religion', 'My Incredible Religion', 'myreligion.tk', 'Convert your ideas into your Religion', 'http://quotesideas.com/wp-content/uploads/2015/02/Religion-quote-on-background-hd-2015.jpeg', 'http://www.allahsword.com/images/books/comparative_religion/comparative_religion_main.png', 'fa-hand-o-up', 'Belief', 'Believe in your own religion. Your views are your religion.', 'fa-gavel', 'Leadership', 'Be a leader of your own religion.', 'fa-book', 'Holy Book', 'Create your holy book by adding your views as verses.', 'fa-user', 'Founder', 'You are the founder of your own religion.', NULL, 'Religious Slogan', 'Science without religion is lame, religion without science is blind.', 'about.php', 'Religious Belief', '<ul class=\"feature-list\">\r\n<li><i class=\"glyphicon glyphicon-ok\"></i> Faith - It does not make things easy, it makes them possible.</li>\r\n<li><i class=\"glyphicon glyphicon-ok\"></i> Free Trip to heaven. Details Inside.</li>\r\n<li><i class=\"glyphicon glyphicon-ok\"></i> God can hear you.</li>\r\n<li><i class=\"glyphicon glyphicon-ok\"></i> God has a great plan for your life.1</li>\r\n<li><i class=\"glyphicon glyphicon-ok\"></i> God is stronger than my circumstances.</li>\r\n</ul>', 'https://www.prageru.com/sites/default/files/courses/badges/badge_religioustolerancemadeinamerica.png', 'More Religious Belief', '<ul class=\"feature-list\">\r\n<li><i class=\"glyphicon glyphicon-ok\"></i> I believe in God. Never panic, just pray.</li>\r\n<li><i class=\"glyphicon glyphicon-ok\"></i> If God is for us, Who can be against us?</li>\r\n<li><i class=\"glyphicon glyphicon-ok\"></i> Know God, Know Peace; No God, No Peace.</li>\r\n<li><i class=\"glyphicon glyphicon-ok\"></i> People are like tea bags ï¿½ you have to put them in hot water before you know how strong they are.</li>\r\n</ul>', 'http://i.dailymail.co.uk/i/pix/2017/03/10/14/3E234A1D00000578-4301306-image-a-33_1489155831037.jpg', '[{\"name\":\"Manish Khanchandani\",\"image\":\"http:\\/\\/lifereminder.tk\\/images\\/mk.jpg\",\"designation\":\"Sr. Web Developer\",\"fb\":\"https:\\/\\/www.facebook.com\\/nkhanchandani7\",\"tw\":\"https:\\/\\/twitter.com\\/ManishK92468643\",\"ln\":\"\",\"gp\":\"https:\\/\\/plus.google.com\\/u\\/0\\/100546875099861959996\",\"description\":\"15 years of experience in Web Development. Working on Angular JS, React JS, PHP, Mysql, Javascript, Bootstrap CSS, etc.\"},{\"name\":\"Carrie Parecki\",\"image\":\"http:\\/\\/carrieparecki.com\\/project2017\\/lr\\/images\\/2k_carrie_parecki_square.jpg\",\"designation\":\"Webmaster\",\"fb\":\"https:\\/\\/www.facebook.com\\/carrie.dinitz\",\"tw\":\"https:\\/\\/twitter.com\\/CarrieParecki\",\"ln\":\"\",\"gp\":\"https:\\/\\/plus.google.com\\/u\\/0\\/107540902803960382512\",\"description\":\"Over 10 years of professional experience as a Webmaster, Web assistant, UI Technical Artist and Web and Graphic Designer using HTML, CSS, Dreamweaver, Drupal, Photoshop, and the latest web and 3D technologies.\"},{\"name\":\"Kate\",\"image\":\"http:\\/\\/localhost\\/project100\\/0001_myreligion\\/images\\/kate.jpg\",\"designation\":\"Web developer\",\"fb\":\"https:\\/\\/www.facebook.com\\/kate.liu.733\",\"tw\":\"https:\\/\\/twitter.com\\/earlybirdcatcat\",\"ln\":\"\",\"gp\":\"http:\\/\\/google.com\\/\",\"description\":\" Bachelor\\u2019s degree and Certification in Graphic Design, Certification in Web Site Design, and have a total work experience of 7 years. I am well versed in different areas of front-end web design\\/development and have successfully designed solutions for print media and websites.\\r\\n\\r\\n\"},{\"name\":\"Tamilselvan Karunanidhy\",\"image\":\"http:\\/\\/localhost\\/project100\\/0001_myreligion\\/images\\/tamil.jpg\",\"designation\":\"Senior Software QA Engineer\",\"fb\":\"https:\\/\\/www.facebook.com\\/tkarunanidhy\",\"tw\":\"https:\\/\\/twitter.com\\/tamus1983\",\"ln\":\"\",\"gp\":\"https:\\/\\/plus.google.com\\/u\\/0\\/+tamilselvankarunanidhy\",\"description\":\"10 years of experience in QA, testing & Automation for SaaS, API, iOS and Android Apps\"}]', NULL, 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Maecenas volutpat, sem quis ornare bibendum, metus odio commodo ante, sit amet finibus sapien nibh vel augue. Lorem ipsum dolor sit amet, consectetur adipiscing elit. Maecenas risus nunc, tristique ac mi non, elementum venenatis elit. Nullam ornare laoreet justo, in dignissim ligula faucibus sit amet.\r\n\r\nSuspendisse tristique diam eu ex dapibus, non finibus quam pellentesque. Fusce eleifend magna vitae sem maximus, eu luctus eros finibus. Aliquam urna leo, finibus vel turpis eget, pretium hendrerit metus. Sed a leo elementum, tempor purus at, lacinia lorem. Praesent porttitor sed ipsum at consequat. Nunc\r\n\r\nLorem ipsum dolor sit amet, consectetur adipiscing elit. Maecenas risus nunc, tristique ac mi non, elementum venenatis elit. Nullam ornare laoreet justo, in dignissim ligula faucibus sit amet.', 'http://lifereminder.tk/images/puzzle.jpg', '89 West Main st Merrimac, MA 01860', '(555) 555-5555', 'site@myreligion.tk', 'Monday - Friday: 9.00 am to 5.00 pm', NULL, 'manishkk74@gmail.com', 12, 13);
